# This Python file uses the following encoding: utf-8# !/usr/bin/env pythonimport osimport djangoos.environ.setdefault("DJANGO_SETTINGS_MODULE", "proj_hkcm.settings")django.setup()from hkcm.models import DistrictsForAllLocationsinLocaList, latlngMappingDistrictsif __name__ == "__main__":    """    Purpose : update district fields in cmdata table.    Author  : Pharrell.zx    Date    : 2017 Feb 6 """    list_one = DistrictsForAllLocationsinLocaList.objects.all()    list_two = latlngMappingDistricts.objects.all()    for x in list_one:        # noinspection PyBroadException        try:            id_one = x.id            corresponding_entry_in_table_two = \                latlngMappingDistricts.objects.get(id_in_d=id_one)            dist = corresponding_entry_in_table_two.Districts            x.Districts = dist            x.save()        except:            # print(x.id)            # print(x.location)            x.Districts = "out_of_hk"            x.save()