# This Python file uses the following encoding: utf-8# !/usr/bin/env pythonimport csvimport osimport djangoos.environ.setdefault("DJANGO_SETTINGS_MODULE", "proj_hkcm.settings")django.setup()from hkcm.models import DistrictsClassificationfrom proj_hkcm.settings import BASE_DIRif __name__ == "__main__":    """this file is for importing the classification of existing    data in table cmdata    together with generated districts    to the database table named 'districtsclassification'    Author  : Pharrell.zx    Date    : 2017 Feb 6 """    with open(str(BASE_DIR) + '/txt_csv_files/pointInPolygon20170206.csv', 'rb') as ifile:        reader = csv.reader(ifile)        header = "undefined"        rownum = 0        for row in reader:            if rownum == 0:                header = row            else:                colnum = 0                id_in_cmdata = 0                lat = 0                lng = 0                dis = 0                for col in row:                    if header[colnum] == 'id':                        id_in_cmdata = col                    elif header[colnum] == 'lat':                        lat = col                    elif header[colnum] == 'lon':                        lng = col                    elif header[colnum] == 'DISTRICT_E':                        dis = col                    else:                        pass                    colnum += 1                record = DistrictsClassification(id_in_cmdata=id_in_cmdata,                                                 latitude=lat,                                                 longitude=lng,                                                 Districts=dis)                record.save()            rownum += 1    ifile.close()